import React from "react";
import {} from "@mui/material";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardActions from "@mui/material/CardActions";
import Button from "@mui/material/Button";
import { getAllDeviceEvents } from "../reducers";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { generatePath, Link as RouterLink, Route } from "react-router-dom";
import { deviceEvents, fetchDeviceEvents, fetchFields } from "../reducers";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import Collapse from "@mui/material/Collapse";
import Typography from "@mui/material/Typography";
import Timestamp from "../components/Timestamp";

import { Box } from "@mui/material";

function Device() {
  const [expandedId, setExpandedId] = React.useState(-1);
  //this opens every card but needs to open indivual cards
  const handleExpandClick = (i) => {
    setExpandedId(expandedId === i ? -1 : i);
  };

  const EVENT_PAGE_PATH = "/event-:id";
  const events = useSelector(getAllDeviceEvents);
  const dispatch = useDispatch();
  React.useEffect(
    /**
     * Mock api call to get device events
     */
    () => {
      dispatch(fetchDeviceEvents());
    },
    [dispatch]
  );
  React.useEffect(
    /**
     * Mock API call to get field data
     */
    (): void => {
      dispatch(fetchFields());
    },
    [dispatch]
  );

  console.log(events);
  return (
    <Box
      sx={{
        display: "flex",
        flexWrap: "wrap",
        gap: "5px",
        justifyContent: "space-evenly"
      }}
    >
      {events.map((event, i) => (
        <Card key={event.id} sx={{ minWidth: 275, maxWidth: 300 }}>
          <h1 className="deviceTitle">{event.device_alias}</h1>
          <CardActions>
            <Button
              component={RouterLink}
              to={generatePath(EVENT_PAGE_PATH, { id: event.id })}
              size="small"
            >
              Learn More
            </Button>
          </CardActions>
          <CardActions disableSpacing>
            <ExpandMoreIcon
              onClick={() => handleExpandClick(i)}
              aria-expanded={expandedId === i}
              aria-label="show more"
            />
          </CardActions>
          <Collapse in={expandedId === i} timeout="auto" unmountOnExit>
            <CardContent>
              <Typography paragraph>Method:</Typography>
              <Typography paragraph>
                Heat 1/2 cup of the broth in a pot until simmering, add saffron
                and set aside for 10 minutes.
              </Typography>
            </CardContent>
          </Collapse>
        </Card>
      ))}
    </Box>
  );
}

export default Device;
